sql.content:
select right_value from base_data_rel 
	where left_key=#{leftKey} and left_value=#{leftValue} and right_key=#{rightKey} and project_code=#{projectCode} and tenant_id=#{tenantId}
union
select right_value from base_data_rel 
	where left_key=#{leftKey} and left_value=#{leftValue} and right_key=#{rightKey} and project_code is null and tenant_id=#{tenantId}
