switch.if:
select right_value from base_data_rel where left_key=#{parentKey} and left_value=#{parentValue} and right_key=#{childKey} and project_code is null and tenant_id=#{tenantId} 

	union
	select right_value from base_data_rel where left_key=#{parentKey} and left_value=#{parentValue} and right_key=#{childKey} and project_code=#{projectCode} and tenant_id=#{tenantId}

switch.else:
select left_value from base_data_rel where right_key=#{parentKey} and right_value=#{parentValue} and left_key=#{childKey} and project_code is null and tenant_id=#{tenantId} 

	union
	select left_value from base_data_rel where right_key=#{parentKey} and right_value=#{parentValue} and left_key=#{childKey} and project_code=#{projectCode} and tenant_id=#{tenantId}
